import { useRouter } from "next/router";
import Image from "next/image";
import { Button } from "@mui/material";
import Head from "next/head";
import { useGlobalContext } from "../layouts/LayoutDefault/context";
import { getLayoutDefault } from "../layouts/LayoutDefault/LayoutDefault";
import { PageHead } from "../components/PageHead/PageHead";

const Login = (): JSX.Element => {
  const { googleSignIn, googleSignOut, currentUser } = useGlobalContext();

  const router = useRouter();

  if (currentUser) {
    router.push("/");
  }

  return (
    <div
      style={{
        width: "100%",
        height: "100vh",
        display: "flex",
        justifyContent: "center",
        alignItems: "center",
      }}
    >
      <PageHead title={"Country Insight - Login"} content={"Login page"} />

      {currentUser ? (
        <Button variant="outlined" onClick={googleSignOut}>
          Log out
        </Button>
      ) : (
        <Button variant="outlined" onClick={googleSignIn}>
          <Image
            src={
              "data:image/svg+xml;base64,"
            }
            width={20}
            height={20}
            alt={"Google"}
            style={{ marginRight: "0.5rem" }}
          />
          Log in with Google
        </Button>
      )}
    </div>
  );
};

export default Login;

Login.getLayout = getLayoutDefault;
